import React, { useState } from "react";

function Inputtest(props) {
  const [name, setName] = useState("");
  const [kor, setKor] = useState("");
  const [eng, setEmail] = useState("");
  //람다함수 - 일반함수의 경우 생성자에 바인딩이라는 작업을 해야한다.
  const nameChange = (e) => {
    // 인자가 = 발생한 이벤트에 대한 모든 정보
    // console.log(e.target.value);//키를 누른 값이 저장되어 있다.
    setName(e.target.value); // name변수의 값이 바뀐다.
  };

  const ageChange = (e) => {
    setAge(e.target.value);
  };

  const emailChange = (e) => {
    setEmail(e.target.value);
  };

  let mystyle = {
    color: "black",
    backgroundColor: "white",
    fontSize: "5pt",
    padding: "10px 5px 10px 5px",
  };

  return (
    <div>
      이름 : <input style={mystyle} type="text" onChange={nameChange} /> <br />
      나이 : <input style={mystyle} type="text" onChange={ageChange} /> <br />
      이메일 : <input style={mystyle} type="text" onChange={emailChange} />{" "}
      
      <br />
      <p>
        {name} {age} {email}
      </p>
    </div>
  );
}
export default Inputtest;
