/* Fortest1.js*/
import React, { useState } from "react";

function Fortest1(props) {
  const [fruitList] = useState(["사과", "배", "포도", "수박", "머루"]);
  const [fruit, setFruit] =useState("");
 
 //input 태그에서 값 입력하면  fruit 변수에 값을 저장한다.
  const onChange= (e)=>{
    setFruit(e.target.value);
  }

   const goAppend=()=>{
     //배열에  push함수 사용못함, 원래 배열에 데이터 추가. 
     //배열 자체를 새로 만들어 바꿔치기를 해야한다.  
     //push - 원래 배열 메모리에 추가
     //concat - 새로운 배열을 만들어서 기존 배열 내용복사하고 하나에 추가   
    setFruitList(fruitList.concat(fruit));
    setFruit("");
   }


   const goSelect=(index)=>{
     alert(fruitList[index]);
   }



  return (
    <div>
      <ul>
        {fruitList.map((item, index) => {
          return (
            <li key={index}>
              <a href="#none" onClick={()=>{goSelect(index)}}>{item}</a>
         
            </li>
          );
        })}
      </ul>
    </div>
  );
}

export default Fortest1;
