import "bootstrap/dist/css/bootstrap.min.css"; //부트스트랩 라이브러리.
import React, { useState, useEffect } from "react"; // 함수기반 컴포터는 할려한다면.
import axios from "axios";
import { SERVERIP } from "../../CommonUtil";
import { Link, useNavigate } from "react-router-dom";

function BoardWrite(props) {
  let history = useNavigate();

  const [heroName, setHeroName] = useState("");
  const [heroDesc, setHeroDesc] = useState("");

  const nameChange = (e) => {
    setHeroName(e.target.value);
  };

  const descChange = (e) => {
    setHeroDesc(e.target.value);
  };

  //서버로 전송하기
  const postData = () => {
    //데이터를 json으로 묶어서 보내야 한다.
    let data = { hero_name: heroName, hero_desc: heroDesc };
    axios
      .post(SERVERIP + "/hero/write", data)
      .then((res) => {
        console.log(res.data);
      })
      .catch((error) => {});
  };

  return (
    <div className="container">
      <table className="table table-hover " style={{ marginTop: "30px" }}>
        <colgroup>
          <col width="25%" />
          <col width="*" />
        </colgroup>

        <tbody>
          <tr>
            <td>제목</td>
            <td>
              <div className="mb-3" style={{ marginTop: "13px" }}>
                <input
                  type="text"
                  className="form-control"
                  id="title"
                  name="title"
                  placeholder="제목을 입력하세요"
                />
              </div>
            </td>
          </tr>
          <tr>
            <td>작성자</td>
            <td>
              <div className="mb-3" style={{ marginTop: "13px" }}>
                <input
                  type="text"
                  className="form-control"
                  id="writer"
                  name="writer"
                  placeholder="이름을 입력하세요"
                  onChange={nameChange}
                />
              </div>
            </td>
          </tr>
          <tr>
            <td>업적</td>
            <td>
              <div className="mb-3" style={{ marginTop: "13px" }}>
                <textarea
                  className="form-control"
                  rows="5"
                  id="contents"
                  name="contents"
                  onChange={descChange}
                ></textarea>
              </div>
            </td>
          </tr>
        </tbody>
      </table>

      <div className="container mt-3" style={{ textAlign: "right" }}>
        <Link className="btn btn-secondary" onClick={postData}>
          등록
        </Link>{" "}
        &nbsp;&nbsp;
        <Link className="btn btn-secondary">취소</Link>
      </div>
    </div>
  );
}
export default BoardWrite;
